/**
 * Module 'foo'  will never have to know which logging/debugging tool you will use.
 * The module developer will never have to decide which logging / debuggin tool he wants to use
 * He just decides to use console, since 'Konsole' can override console calls and catches all calls
 */


exports.doSomething = function () {
    console.log("I am doing something");
}

exports.doSomethingElse = function () {
    console.warn("Nooooo!");
}


setInterval(function () {
    console.log("Did this and that");
    exports.doSomethingElse();
}, 5000);